import React,{Component} from 'react'
class Contact extends Component{
   constructor(props){
       super(props);
       this.state={
           items:[],
           isLoaded:false,
       }
   }
   componentDidMount(){
       fetch('https://jsonplaceholder.typicode.com/users')
       .then(res=>res.json())
       .then(json=>{
           this.setState({
               isLoaded:true,
               items:json,
           })
       })
   }
    render(){
        var{isLoaded,items}=this.state;
        if(!isLoaded)
        {
            return <div>Loading</div>
        }
        else{
        return(
<div>
    <ul>

        {items.map(items=>(
<li key={items.id}>
  Name: {items.name}
  Email:{items.email} 
</li>
        ))}
    </ul>
</div>
        )
    }
}
}
export default Contact;
